<?php
    include '../php/DatabaseManager.php';
    include '../assets/view_builder.php';
    include '../helper/helper.php';
    $filesx = GetDataFiles();
    $jumleh = 0;
    foreach($filesx as $file){
        $con_stat = $file->GetStatus();
        $now = time();
        $tglmulai = strtotime($file->date);
        if($con_stat>5){
                                $tglkontrakRampung = strtotime($file->tglBerakhir);
                                $datediffx = $tglkontrakRampung - $now;
                                $voix = round($datediffx / (60 * 60 * 24));
                                if($voix<60&&$voix>=0){
                                  $jumleh++;
                                }
        }
    }

if(!AllowAcces()){
    header("location:../assets/login.php");
}
